import"./hoisted.BpwPFlAF.js";class s extends HTMLElement{connectedCallback(){const t=this.dataset.url;if(!t)return;const{owner:e,repo:o}=this.getRepoInfoFromUrl(t);fetch(`https://api.github.com/repos/${e}/${o}`).then((t=>{if(!t.ok)throw new Error("Failed to fetch repository info.");return t.json()})).then((t=>{this.querySelector(".repo-user span").textContent=t.owner.login,this.querySelector(".repo-fork span").textContent=t.forks_count,this.querySelector(".repo-star span").textContent=t.stargazers_count,this.querySelector(".repo-watch span").textContent=t.subscribers_count})).catch((t=>{console.error("Error fetching repository info:",t)}))}getRepoInfoFromUrl(t){const e=t.split("/");return{owner:e[e.length-2],repo:e[e.length-1]}}}customElements.define("github-info",s);